mapper.delete:
update t_accessory_url
set removed = 1, updated_by = #{updatedBy}, updated_time = #{updatedTime}
where id = #{id};

mapper.select:
select
id,
provider_name,
send_order_weight
from t_brand
where brand_id = #{brandId}
and type_id = #{typeId}
and removed = 0;

select
id,
provider_name,
send_order_weight
from t_brand
where brand_id = #{brandId}
and type_id = #{typeId}
and removed = 0
and send_order_weight != 0
and id != #{id}
order by send_order_weight asc;

select
id,
provider_name,
buckle_point
from t_brand
where brand_id = #{brandId}
and type_id = #{typeId}
and removed = 0
and send_order_weight != 0
and id != #{id}
order by buckle_point desc;

mapper.update:
update t_brand
set send_order_weight = #{sendOrderWeight}, updated_by = #{updatedBy}, updated_time = #{updatedTime}
where id = #{id};

update t_brand


        buckle_point = #{bucklePoint},


        updated_by = #{updatedBy},


        updated_time = #{updatedTime}


where id = #{id};

mapper.select:
select id, provider_id_fk, provider_name, buckle_point from t_brand
provider_name like concat('%', #{providerName}, '%')
where removed = 0 and trademark_status != 2 and brand_id = #{brandId} and type_id = #{typeId} limit #{pageIndex}, #{pageSize};

select count(1) from t_brand
provider_name like concat('%', #{providerName}, '%')
where removed = 0 and trademark_status != 2 and brand_id = #{brandId};
