mapper.select:
SELECT t_provider.provider_id_fk, t_provider.company_name, t_provider.company_email, CASE manage_status WHEN 0 THEN '新进商家' WHEN 1 THEN '储备商家' WHEN 2 THEN '正常商家' WHEN 3 THEN '监督整改' WHEN 4 THEN '黑名单' ELSE '暂停合作' END AS manage_status, CASE manage_method WHEN 0 THEN '经销' ELSE '代销' END AS manage_method, t_provider.platform_principal, concat_ws( ',', CASE settlement_interval WHEN 0 THEN '日' WHEN 1 THEN '周' WHEN 2 THEN '半月' WHEN 3 THEN '月' ELSE '年' END, NULL ) AS settlement_interval, CASE provider_auth_level WHEN 0 THEN '品牌商' WHEN 1 THEN '一级经销商' WHEN 2 THEN '二级经销商' WHEN 3 THEN '三级经销商' ELSE '代销' END AS provider_auth_level, GROUP_CONCAT( t_brand.brand_name ) AS brand_name, GROUP_CONCAT( t_brand.type_name ) AS type_name, DATEDIFF(t_provider.contact_end_date,curdate()) mm, CASE WHEN DATEDIFF(t_provider.contact_end_date,curdate()) > 30 THEN '正常' WHEN DATEDIFF(t_provider.contact_end_date,curdate()) = 30 THEN '即将过期' WHEN 30 > DATEDIFF(t_provider.contact_end_date,curdate()) > 15 THEN '即将过期' WHEN DATEDIFF(t_provider.contact_end_date,curdate()) <= 15 THEN '即将过期' WHEN DATEDIFF(t_provider.contact_end_date,curdate()) < 0 THEN '过期' ELSE '过期' END AS contract_status, t_provider.contact_end_date, CASE is_contact_extension WHEN 0 THEN '续约' ELSE '不续约' END AS is_contact_extension FROM t_provider LEFT JOIN t_finance ON t_provider.provider_id_fk = t_finance.provider_id_fk LEFT JOIN t_brand ON t_provider.provider_id_fk = t_brand.provider_id_fk where 1=1
and company_name = #{companyName,jdbcType=VARCHAR}
AND manage_status = #{manageStatus,jdbcType=INTEGER}
AND manage_method = #{manageMethod,jdbcType=INTEGER}
AND contract_status = #{contractStatus,jdbcType=INTEGER}
AND brand_name = #{brandName,jdbcType=VARCHAR}
GROUP BY t_provider.provider_id_fk limit ${pageIndex}, ${pageSize}
